/* ==================== VIDEO RESPONSIVE ==================== */
.video-section {
  margin: 32px 0;
  text-align: center;
}

.video-container {
  position: relative;
  width: 100%;
  max-width: 900px; /* Limit width for better viewing on large screens */
  margin: 0 auto;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  border: 1px solid var(--border-color);
  background: #000;
}

.video-container video {
  width: 100%;
  height: auto;
  display: block;
}
